import React from "react";

function EditItem({ itemDate, isUpdating }) {

    const handleSubmit = (e) => {
        const concept = document.getElementById("concept");
        const amount = document.getElementById("amount");

        fetch(`http://localhost:3001/edit/4`, {
          method: "PUT",
          mode: "cors",
          body: JSON.stringify({
            concept: concept.value,
            amount: amount.value,
          }),
          headers: { "Content-Type": "application/json" },
        })
        .then((res) => res.json())
          .then((res) => {
            if (res.success) {
              alert("data is updated!");
            }
           });
        e.preventDefault()   
    };

        return(
            <div>
            {isUpdating === true ?
                <div>
                    <h3>Update Item!</h3>
                    <form>
                        <label>
                          Amount
                            <input               
                            maxLength="10"
                            required
                            type="number"
                            name="amount"
                            id="amount"
                            placeholder={itemDate.amount}
                            />
                        </label>
                        <label>
                          Concept
                            <input
                            minLength="3"
                            maxLength="15"
                            required
                            type="text"
                            name="concept"
                            id="concept"
                            placeholder={itemDate.concept}
                            />
                        </label>
                        <button onClick={handleSubmit(itemDate.id)} type="submit"> Set data </button>
                    </form>
                </div>
          : ''} 
          </div>
        )
}

export default EditItem;
