import React, { useEffect, useState } from "react";
import './itemList.css';

const ItemList = () => {
    const url = 'http://localhost:3000/movements'
    const [items, setItems] = useState()
    const fetchApi = async () => {
      const response = await fetch(url)
      const responseJSON = await response.json()
      setItems(responseJSON)
    }
    useEffect(() => {
      fetchApi()
    }, [])
    const totalAmountListener = () => {
        let totalAmount;
        for(let item in items){
          totalAmount = totalAmount + item[3];
        }
        return totalAmount;
    }
    const getDate = (date) => {
      let parseDate = date.substring(0, 10);
      return parseDate;
    }
    console.log(items);
  return (
    <div>
        <div>
        <h2>
            ITEM LIST
        </h2>
        <div>
            {totalAmountListener()}
        </div>
        </div>
        <ul>
        { !items ? 'Loading...' : 
          items.slice(items.length-10).reverse().map((item,index) => {
              return <li className={item.type===1 ? 'item-green' : 'item-red'} key={index}>{item.concept} | {getDate(item.date)} | {item.type===1 ? '+' : '-'}${item.amount}</li>       
          })
        }
        </ul>
    </div>
  );
};

export default ItemList;
