import React, {useState} from "react";


const ItemUpdate = ({itemId}) => {

    const [item, setItem] = useState();
    const fetchApi = async (id) => {
        const response = await fetch(`http://localhost:3001/movements/${id}`);
        const responseJSON = await response.json();
        setItem(responseJSON);
    };

    if(!itemId){
        fetchApi(itemId)
        return(
            <div>
                <h3>Update Item!</h3>
                <form>
                    <label>
                        <input               
                        maxLength="10"
                        required
                        type="number"
                        name="amount"
                        id="amount"
                        value={item.amount}
                        />
                    </label>
                    <label>
                        <input
                        minLength="3"
                        maxLength="15"
                        required
                        type="text"
                        name="concept"
                        id="concept"
                        value={item.concept}
                        />
                    </label>
                </form>
            </div>
        )
    }else{
        return null;
    }
}
export default ItemUpdate;
