import React, { useEffect, useState } from "react";
import deleteBtn from '../../../img/btn-delete.png';
import updateBtn from '../../../img/btn-update.png';
import './itemList.css';

const ItemList = () => {
    const url = 'http://localhost:3000/movements'
    const [items, setItems] = useState()
    const fetchApi = async () => {
      const response = await fetch(url)
      const responseJSON = await response.json()
      setItems(responseJSON)
    }
    useEffect(() => {
      fetchApi()
    }, [])

    const itemUpdate = (item) => {
      this.setState({
        body:{
          id: item.id,
          amount: item.amount,
          type: item.type,
          concept: item.concept,
          date: item.date
        }
      }
      )
    }

    const totalAmountListener = (items) => {
      let totalAmount
      items.map((item) => {
      if(item.type===0) {
        totalAmount= item.reduce((sum, value) => (typeof value.amount == "number" ? sum + value.amount : sum), 0);
      }
      else{
        totalAmount= totalAmount - item.amount;
      }})
      return totalAmount;
    }
    const getDate = (date) => {
      let parseDate = date.substring(0, 10);
      return parseDate;
    }
    console.log(items);
  return (
    <div className="list-item">
        <div>
        <h2>
            ITEM LIST
        </h2>
        <div>
            {totalAmountListener(items)}
        </div>
        </div>
        <hr/>
        <ul>
        { !items ? 'Loading...' : 
          items.slice(items.length-10).reverse().map((item,index) => {
              return (<div className={item.type===0 ? 'item-green' : 'item-red'} key={index}>
                <button className="update-btn"><img className="update-img" alt="" src={updateBtn}/></button>
                {item.concept} | {getDate(item.date)} | {item.type===0 ? '+' : '-'}${item.amount}
                <button className="delete-btn"><img className="delete-img" alt="" src={deleteBtn}/></button>
                </div>
                )       
          })
        }
        </ul>
    </div>
  );
};

export default ItemList;
