import React, { useEffect, useState } from "react";
import "./itemList.css";

const ItemList = () => {
  const url = "http://localhost:3001/movements";
  const [items, setItems] = useState();
  const fetchApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setItems(responseJSON);
  };
  useEffect(() => {
    fetchApi();
  }, [items]);

  const onSubmit = (e) => {
    const concept = document.getElementById("concept");
    const amount = document.getElementById("amount");

    fetch("http://localhost:3001/add", {
      method: "PUT",
      mode: "cors",
      body: JSON.stringify({
        concept: concept.value,
        amount: amount.value,
      }),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((res) => {
        if (res.success) {
          alert("data is update!");
        }
      });

    e.preventDefault();
  };

  const [itemDate, setItemDate] = useState([]);
  let totalAmount = 0;
  const [isUpdating, setIsUpdating] = useState(false);

  const itemDelete = async (id) => {
    await fetch(`http://localhost:3001/delete/${id}`, {
      method: 'DELETE',
    })
  }

  const getItem = (item) => {
    setItemDate(item)
    setIsUpdating(true);
  }

  const getDate = (date) => {
    let parseDate = date.substring(0, 10);
    return parseDate;
  };

  return (
    <div>
    <div className="list-item">
      <div>
        <h2>ITEM LIST</h2>
      </div>
      <hr />
      <div>
        {!items? "Loading..." : items
              .slice(items.length - 10)
              .reverse()
              .map((item, index) => {
                if(item.type === 0){
                  totalAmount= totalAmount + item.amount;
                } else{
                  totalAmount= totalAmount - item.amount;
                }
                return (
                  <div>
                    <div
                      className={item.type === 0 ? "item-green" : "item-red"}
                      key={index}
                    >
                      <button
                        className="update-btn"
                        onClick={() => getItem(item)}
                      >
                        Edit
                      </button>
                      {item.concept} | {getDate(item.date)} |{" "}
                      {item.type === 0 ? "+" : "-"}${item.amount}
                      <button className="delete-btn" onClick={()=>itemDelete(item.id)}>
                        Delete
                      </button>
                    </div>
                    <hr />
                  </div>
                );
              })
              }
        <div>MONTO TOTAL: {totalAmount}</div>
      </div>
    </div>
      {isUpdating === true ?
            <div>
                <h3>Update Item!</h3>
                <form>
                    <label>
                        <input               
                        maxLength="10"
                        required
                        type="number"
                        name="amount"
                        id="amount"
                        value={itemDate.amount}
                        />
                    </label>
                    <label>
                        <input
                        minLength="3"
                        maxLength="15"
                        required
                        type="text"
                        name="concept"
                        id="concept"
                        value={itemDate.concept}
                        />
                    </label>
                    <button type="submit"> Set data </button>
                </form>
            </div>
      : ''} 
    </div>
  );
};

export default ItemList;
