import React, { useEffect, useState } from "react";

const ItemList = () => {
    const url = 'http://localhost:3000/movements'
    const [items, setItems] = useState()
    const fetchApi = async () => {
      const response = await fetch(url)
      const responseJSON = await response.json()
      setItems(responseJSON)
    }
    useEffect(() => {
      fetchApi()
    }, [])
    const totalAmountListener = () => {
        let totalAmount;
        for (let index = 0; index < items.map().length; index++) {
            totalAmount = totalAmount + items.amount;
        }
        return totalAmount;
    }
  return (
    <div>
        <div>
        <h2>
            ITEM LIST
        </h2>
        <div>
            {totalAmountListener()}
        </div>
        </div>
        <ul>
        { !items ? 'Loading...' : 
          items.slice(items.length-10).reverse().map((item,index) => {
            return <li key={index}>{item.concept} | {item.type} | {item.type===1 ? '+' : '-'}${item.amount}</li>
          })
        }
        </ul>
    </div>
  );
};

export default ItemList;
