        fetch ('http://localhost:3050/add', {
            method: 'POST',
            mode: 'cors',
            body: JSON.stringify({
                concept: concept.value,
                type: type.value,
                amount: amount.value
            }),
            headers: { 'Content-Type': 'application/json' }
          })
          .then(res => res.json())
          .then(res => {
            if (res.success) { 
              alert('Categoría creada');
            }
