import React, { useState } from "react";
import './edititem.css'

function EditItem({ itemDate, isUpdating }) {

    const [concept, setConcept] = useState();
    const [amount, setAmount] = useState();

    const handleSubmit = async (e) => {
        e.preventDefault()   

        await fetch(`http://localhost:3001/edit/${itemDate.id}`, {
          method: "PUT",
          mode: "cors",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            concept: concept,
            amount: amount,
          }),
        })
        .then(async res => await res.json())
          .then(res => {
            if (!res.ok) {
                const error = (res && res.message) || res.status;
                return Promise.reject(error);
            }
        })
        .catch(error => {
            console.error('There was an error!', error);
        });
    };

        return(
            <div className="disp-center">
            {isUpdating === true ?
                <div className="Form-border">
                    <h3>Update Item!</h3>
                    <form onSubmit={handleSubmit} className='Form-edit'>
                        <label className="Form-edit-label">
                          Amount
                            <input    
                            className="Form-edit-input"           
                            maxLength="10"
                            required
                            type="number"
                            name="amount"
                            id="amount"
                            placeholder={itemDate.amount}
                            value={amount}
                            onChange= {({target: {value}})=> setAmount(value)}
                            />
                        </label>
                        <label className="Form-edit-label">
                          Concept
                            <input
                            className="Form-edit-input" 
                            minLength="3"
                            maxLength="15"
                            required
                            type="text"
                            name="concept"
                            id="concept"
                            placeholder={itemDate.concept}
                            value={concept}
                            onChange= {({target: {value}})=> setConcept(value)}
                            />
                        </label>
                        <button type="submit"> Set data </button>
                    </form>
                </div>
          : ''} 
          </div>
        )
}

export default EditItem;
