import React from "react";

function EditItem({ itemDate, isUpdating }) {

    const handleSubmit = async (e) => {
        e.preventDefault()   
        const concept = document.getElementById("concept");
        const amount = document.getElementById("amount");

        await fetch(`http://localhost:3001/edit/${itemDate.id}`, {
          method: "PUT",
          mode: "cors",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            concept: this.concept.value,
            amount: this.amount.value,
          }),
        })
        .then(async res => await res.json())
          .then(res => {
            if (!res.ok) {
                const error = (res && res.message) || res.status;
                return Promise.reject(error);
            }
        })
        .catch(error => {
            console.error('There was an error!', error);
        });
    };

        return(
            <div>
            {isUpdating === true ?
                <div>
                    <h3>Update Item!</h3>
                    <form onSubmit={handleSubmit}>
                        <label>
                          Amount
                            <input               
                            maxLength="10"
                            required
                            type="number"
                            name="amount"
                            id="amount"
                            placeholder={itemDate.amount}
                            />
                        </label>
                        <label>
                          Concept
                            <input
                            minLength="3"
                            maxLength="15"
                            required
                            type="text"
                            name="concept"
                            id="concept"
                            placeholder={itemDate.concept}
                            />
                        </label>
                        <button type="submit"> Set data </button>
                    </form>
                </div>
          : ''} 
          </div>
        )
}

export default EditItem;
