import React, { useEffect, useState } from "react";

const ItemList = () => {
    const url = 'http://localhost:3000/movements'
    const [items, setItems] = useState()
    const fetchApi = async () => {
      const response = await fetch(url)
      const responseJSON = await response.json()
      setItems(responseJSON)
    }
    useEffect(() => {
      fetchApi()
    }, [])
    const totalAmountListener = () => {
        let totalAmount;
        items?.map((item, index)=>{
            if (index <= items.length) {
                totalAmount = totalAmount + item.amount;
            }
            return totalAmount;
        })
    }
    const getDate = (date) => {
      let parseDate = date.substring(0, 10);
      return parseDate;
    }
  return (
    <div>
        <div>
        <h2>
            ITEM LIST
        </h2>
        <div>
            {totalAmountListener()}
        </div>
        </div>
        <ul>
        { !items ? 'Loading...' : 
          items.slice(items.length-10).reverse().map((item,index) => {
            if(item.type===1){
              return <li className="item-green" key={index}>{item.concept} | {getDate(item.date)} | {item.type===1 ? '+' : '-'}${item.amount}</li>
            } else {
              return <li className="item-red" key={index}>{item.concept} | {getDate(item.date)} | {item.type===1 ? '+' : '-'}${item.amount}</li>
            }

          })
        }
        </ul>
    </div>
  );
};

export default ItemList;
