import React, { useEffect, useState } from "react";
import deleteBtn from "../../../img/btn-delete.png";
import updateBtn from "../../../img/btn-update.png";
import "./itemList.css";

const ItemList = () => {
  const url = "http://localhost:3001/movements";
  const [items, setItems] = useState();
  const fetchApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setItems(responseJSON);
  };
  useEffect(() => {
    fetchApi();
  }, []);

  let totalAmount = 0;

  const itemUpdate = (item) => {
    this.setState({
      body: {
        id: item.id,
        amount: item.amount,
        type: item.type,
        concept: item.concept,
        date: item.date,
      },
    });
  };

  const itemDelete = (id) => {
    fetch(`http://localhost:3001/delete/${id}`, {
      method: 'DELETE',
    })
  }

  /*const totalAmountListener = (items) => {
    let totalAmount = items.reduce(
      (sum, value) =>
        value.type === 0
          ? sum + value.amount
          : sum,
      0
    );
    totalAmount = items.reduce(
      (sum, value) =>
        value.type === 1
          ? sum - value.amount
          : sum,
      0
    );
    return totalAmount;
  };*/

  const getDate = (date) => {
    let parseDate = date.substring(0, 10);
    return parseDate;
  };
  console.log(items);
  return (
    <div className="list-item">
      <div>
        <h2>ITEM LIST</h2>
      </div>
      <hr />
      <div>
        {!items? "Loading..." : items
              .slice(items.length - 10)
              .reverse()
              .map((item, index) => {
                if(item.type === 0){
                  totalAmount= totalAmount + item.amount;
                } else{
                  totalAmount= totalAmount - item.amount;
                }
                return (
                  <div>
                    <div
                      className={item.type === 0 ? "item-green" : "item-red"}
                      key={index}
                    >
                      <button
                        className="update-btn"
                        onClick={() => itemUpdate(item)}
                      >
                        <img className="update-img" alt="" src={updateBtn} />
                      </button>
                      {item.concept} | {getDate(item.date)} |{" "}
                      {item.type === 0 ? "+" : "-"}${item.amount}
                      <button className="delete-btn" onClick={()=>itemDelete(item.id)}>
                        <img className="delete-img" alt="" src={deleteBtn} />
                      </button>
                    </div>
                    <hr />
                  </div>
                );

              })
              }
        <div>MONTO TOTAL: {totalAmount}</div>
      </div>
    </div>
  );
};

export default ItemList;
